function handleTouchMove(articleNo, slideNo, xDiff, yDiff) {


    if ( Math.abs( xDiff ) > Math.abs( yDiff ) ) {/*most significant*/
        if ( xDiff > 0 ) {
            articleNo -= 1;
            slideNo -= 1;
            moveArticlesRight(articleNo, slideNo);
        }
        else {
            articleNo -= 1;
            slideNo -= 1;
            moveArticlesLeft(articleNo, slideNo);
        }
    }
    // else {
    //     if ( yDiff > 0 ) {
    //         /* up swipe */
    //     } else {
    //         /* down swipe */
    //     }
    // }
    /* reset values */
    xDown = null;
    yDown = null;
};


